mapper.insert:
INSERT INTO TB_Acunt (
   AcuntId
 , UserType
 , UserId
 , Pw
 , UseYn
 , RegDt
 , LeaveDt
 , ExpirDt
 , TermsUse
 , TermsPersn
 , TermsEvent
 , InsId
 , InsDt
 , UptId
 , UptDt
) VALUES (
	#{acuntId}
 , #{userType}
 , #{userId}
 , #{pw}
 , #{useYn}
 , #{regDt}
 , #{leaveDt}
 , DATE_FORMAT(DATE_ADD(NOW(), INTERVAL 12 MONTH), '%Y%m%d')
 , #{termsUse}
 , #{termsPersn}
 , #{termsEvent}
 , #{insId}
 , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
 , #{uptId}
 , #{uptDt}
)

mapper.update:
UPDATE TB_Acunt SET
	Pw = #{pw}
				  , UseYn = #{useYn}
				  , RegDt = #{regDt}
				  , LeaveDt = #{leaveDt}
				  , ExpirDt = #{expirDt}
				  , TermsUse = #{termsUse}
				  , TermsPersn = #{termsPersn}
				  , TermsEvent = #{termsEvent}
				  , UptId	= #{uptId}
				  , UptDt	= #{uptDt}
WHERE
	AcuntId = #{acuntId}
