mapper.insert:
INSERT INTO TB_OrgTurn (
	OrgId
	, TurnId
	, UseYn
	, TurnNum
	, TurnReqCnt
	, TurnUseCnt
	, TurnConnCd
	, InsId
	, InsDt
	, UptId
	, UptDt
) VALUES (
	#{orgId}
	, nextval(OrgTurn_TurnId)
	, #{useYn}
	, #{turnNum}
	, #{turnReqCnt}
	, #{turnUseCnt}
	, #{turnConnCd}
	, #{insId}
	, #{insDt}
	, #{uptId}
	, #{uptDt}
)

mapper.update:
UPDATE TB_OrgTurn SET
	UseYn	=	#{useYn}
	, TurnNum	=	#{turnNum}
	, TurnReqCnt	=	#{turnReqCnt}
	, TurnUseCnt	=	#{turnUseCnt}
	, TurnConnCd	=	#{turnConnCd}
	, UptId	=	#{uptId}
	, UptDt	=	#{uptDt}
WHERE
	OrgId = #{orgId}
	AND TurnId = #{turnId}
