mapper.insert:
INSERT INTO TB_QuestPage (
	testId
	, questPageId
	, questPageNm
	, questPageSeq
	, questPageType
	, insId
	, insDt
	, uptId
	, uptDt
) VALUES (
	#{testId}
	, nextval(QuestPage_QuestPageId)
	, #{questPageNm}
	, #{questPageSeq}
	, #{questPageType}
	, #{insId}
	, DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	, null
	, null
)

INSERT INTO TB_Quest (
	testId
	, questPageId
	, questId
	, questNo
	, questCont1
	, questCont2
	, questCont3
	, questType
	, questAttrCd1
	, questAttrCd2
	, waitSec
	, itemColCnt
	, imgColCnt
	, useYn
	, insId
	, insDt
	, uptId
	, uptDt
) VALUES (
	 #{testId}
	 , #{questPageId}
	 , nextval(Quest_QuestId)
	 , #{questNo}
	 , #{questCont1}
	 , #{questCont2}
	 , #{questCont3}
	 , #{questType}
	 , #{questAttrCd1}
	 , #{questAttrCd2}
	 , #{waitSec}
	 , #{itemColCnt}
	 , #{imgColCnt}
	 , #{useYn}
	 , #{insId}
	 , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	 , null
	 , null
)

mapper.update:
UPDATE TB_Test SET
	testNm = #{testNm}
	, seq = #{seq}
	, uptId	= #{uptId}
	, uptDt	= DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
WHERE
	TestId = #{testId}

UPDATE TB_QuestPage SET
	questPageNm = #{questPageNm}
	, questPageSeq = #{questPageSeq}
	, questPageType = #{questPageType}
	, uptId	= #{uptId}
	, uptDt	= DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
WHERE
	QuestPageId = #{questPageId}

UPDATE TB_Quest SET
	questPageId =  #{questPageId}
	, questNo =  #{questNo}
	, questCont1 =  #{questCont1}
	, questCont2 =  #{questCont2}
	, questCont3 =  #{questCont3}
	, questType =  #{questType}
	, questAttrCd1 =  #{questAttrCd1}
	, questAttrCd2 =  #{questAttrCd2}
	, waitSec =  #{waitSec}
	, itemColCnt =  #{itemColCnt}
	, imgColCnt =  #{imgColCnt}
	, useYn =  #{useYn}
	, uptId	= #{uptId}
	, uptDt	= DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
WHERE
	QuestId = #{questId}

DELETE FROM TB_QuestPage
WHERE
	TestId = #{testId}
	AND QuestPageId = #{questPageId}
